import React, { useState } from "react";
import { Button, Modal, Form } from "react-bootstrap";

interface AddQuizModalProps {
  show: boolean;
  handleClose: () => void;
  addQuiz: (title: string, body: string) => void;
}
export const AddQuizModal = ({
  show,
  handleClose,
  addQuiz,
}: AddQuizModalProps) => {
  const [title, setTitle] = useState<string>("Example Quiz");
  const [body, setBody] = useState<string>("Example Description");

  const saveChanges = () => {
    addQuiz(title, body);
    setTitle("Example Quiz");
    setBody("Example Description");
    handleClose();
  };

  return (
    <div>
      <Modal show={show} onHide={handleClose} animation={false}>
        <Modal.Header closeButton>
          <Modal.Title>Add New Quiz</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group controlId="formQuizId">
            <Form.Label>Title: </Form.Label>
            <Form.Control
              value={title}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
                setTitle(e.target.value)
              }
            ></Form.Control>
            <Form.Label>Description: </Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={body}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
                setTitle(e.target.value)
              }
            ></Form.Control>
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => {
              setTitle("Example Quiz");
              setBody("Example Description");
              handleClose();
            }}
          >
            Close
          </Button>
          <Button variant="primary" onClick={saveChanges}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};
